CREATE TYPE IF NOT EXISTS "promotionType" AS ENUM('percentage', 'fixed_amount', 'bonus_buy', 'bundle');

CREATE TABLE IF NOT EXISTS "bulkPriceUpdates" (
	"id" varchar(64) PRIMARY KEY NOT NULL,
	"fileName" varchar(255) NOT NULL,
	"uploadedBy" varchar(64) NOT NULL,
	"recordsProcessed" integer NOT NULL,
	"recordsUpdated" integer NOT NULL,
	"recordsFailed" integer NOT NULL,
	"errorLog" text,
	"createdAt" timestamp DEFAULT now()
);

CREATE TABLE IF NOT EXISTS "promotions" (
	"id" varchar(64) PRIMARY KEY NOT NULL,
	"name" text NOT NULL,
	"description" text,
	"promotionType" "promotionType" NOT NULL,
	"discountValue" numeric(10, 2),
	"bonusPattern" varchar(100),
	"productIds" text,
	"startDate" timestamp NOT NULL,
	"endDate" timestamp NOT NULL,
	"active" boolean DEFAULT true NOT NULL,
	"priority" integer DEFAULT 0,
	"createdAt" timestamp DEFAULT now(),
	"updatedAt" timestamp DEFAULT now()
);

ALTER TABLE "customers" ADD COLUMN IF NOT EXISTS "address" text;
ALTER TABLE "customers" ADD COLUMN IF NOT EXISTS "primaryContact" varchar(100);
ALTER TABLE "customers" ADD COLUMN IF NOT EXISTS "accountsContact" varchar(100);
ALTER TABLE "customers" ADD COLUMN IF NOT EXISTS "accountManager" varchar(100);
ALTER TABLE "customers" ADD COLUMN IF NOT EXISTS "paymentTerms" varchar(50) DEFAULT 'Net 30';

